InputParameters PrecipitateKKSAction::validParams() {
  InputParameters params = Action::validParams();
  params.addClassDescription(
      "Set up the variables required for precipitate growth ");
  params.addParam<unsigned int>(
      "op_num", 1, "specifies the number of order parameters to create");
  params.addParam<std::string>("var_name_base", "eta",
                               "specifies the base name of the variables");
  params.addParam<bool>("add_basic_variables", "true",
                        "For adding the variables cs,cl,c,eta,w ");

  // Get MooseEnums for the possible order/family options for this variable
  MooseEnum families(AddVariableAction::getNonlinearVariableFamilies());
  MooseEnum orders(AddVariableAction::getNonlinearVariableOrders());
  params.addParam<MooseEnum>("family", families,
                             "Specifies the family of FE "
                             "shape function to use for the order parameters");
  params.addParam<MooseEnum>("order", orders,
                             "Specifies the order of the FE "
                             "shape function to use for the order parameters");
  params.addParam<Real>(
      "scaling", 1.0,
      "Specifies a scaling factor to apply to the order parameters");

  return params;
}

PrecipitateKKSAction::PrecipitateKKSAction(const InputParameters &params)
    : Action(params), _op_num(getParam<unsigned int>("op_num")),
      _var_name_base(getParam<std::string>("var_name_base")),
      _add_basic_variables(getParam<bool>("add_basic_variables")),
      _fe_type(
          Utility::string_to_enum<Order>(getParam<MooseEnum>("order")),
          Utility::string_to_enum<FEFamily>(getParam<MooseEnum>("family"))) {}

void PrecipitateKKSAction::act() {
  std::vector<VariableName> basic_variables;
  basic_variables.push_back("eta");
  basic_variables.push_back("c");
  basic_variables.push_back("w");
  basic_variables.push_back("cs");
  basic_variables.push_back("cl");

  if (_add_basic_variables) {
    for (auto var_name : basic_variables) {
      auto type = AddVariableAction::determineType(_fe_type, 1);
      auto var_params = _factory.getValidParams(type);

      var_params.applySpecificParameters(_pars, {"family", "order"});
      var_params.set<std::vector<Real>>("scaling") = {
          getParam<Real>("scaling")};

      // Add variable
      if (_current_task == "add_variable") {
        _problem->addVariable(type, var_name, var_params);
        std::cout << _current_task << ":"
                  << "added variables" << var_name << std::endl;
      }
    }
  }
}```

