This is the header file (OldPostprocessor.h).
    #pragma once

    #include "GeneralPostprocessor.h"

    // Forward Declarations
    class OldPostprocessor;

    template <>
    InputParameters validParams<OldPostprocessor>();

    class OldPostprocessor : public GeneralPostprocessor
    {
    public:
      static InputParameters validParams();

      OldPostprocessor(const InputParameters & parameters);

      virtual void initialize() override;
      virtual void execute() override;
      virtual Real getValue() override;

    protected:
      /// current post-processor value
      const PostprocessorValue & _pps_value;

      /// old post-processor value
      const PostprocessorValue & _pps_value_old;
    };


This is the source code (OldPostprocessor.C).


    #include "OldPostprocessor.h"

    #include "libmesh/parallel.h"

    registerMooseObject("ermineApp", OldPostprocessor);

    defineLegacyParams(OldPostprocessor);

    InputParameters
    OldPostprocessor::validParams()
    {
      InputParameters params = GeneralPostprocessor::validParams();
      params.addClassDescription("A postprocessor object that returns a value of zero.");
      params.addRequiredParam<PostprocessorName>("postprocessor", "The name of the postprocessor");
      return params;
    }

    OldPostprocessor::OldPostprocessor(const InputParameters & parameters)
      : GeneralPostprocessor(parameters),
      _pps_value(getPostprocessorValue("postprocessor")),
      _pps_value_old(getPostprocessorValueOld("postprocessor"))
    {
    }

    Real
    OldPostprocessor::getValue()
    {
      if(_t_step == 0)
      {
        return 0;
      }
      else
      {
        return _pps_value_old;
      }
    }

