void
ADCapillaryStress::computeQpProperties()
{
  auto & S = _capillary_stress[_qp];
  S.zero();

  const ADReal nx = _grad_v[_qp](0);
  const ADReal ny = _grad_v[_qp](1);
  const ADReal nz = _grad_v[_qp](2);

  S(0, 0) += _kappa[_qp] * nx * nx;
  S(0, 1) += _kappa[_qp] * nx * ny;
  S(1, 1) += _kappa[_qp] * ny * ny;
  S(0, 2) += _kappa[_qp] * nx * nz;
  S(1, 2) += _kappa[_qp] * ny * nz;
  S(2, 2) += _kappa[_qp] * nz * nz;

  // fill in symmetrically
  S(1, 0) = S(0, 1);
  S(2, 0) = S(0, 2);
  S(2, 1) = S(1, 2);
}

