#include "EcorrPost.h"

#include "libmesh/quadrature.h"

//registerMooseObject("corrosionApp", EcorrPost);

InputParameters
EcorrPost::validParams()
{
  InputParameters params = SidePostprocessor::validParams();
  params.addCoupledVar("T",298.15,"T");
  params.addRequiredCoupledVar("C9","HS-");
  params.addCoupledVar("C1",0,"C1");
  params.addCoupledVar("C0",0,"C0");
  params.addCoupledVar("C3",0,"C3");
  params.addCoupledVar("C6",0,"C6");
  return params;
}

EcorrPost::EcorrPost(const InputParameters & parameters)
  : SidePostprocessor(parameters),
	_qp(0),
	_integral_value(0),
	_qp_integration(true),
	_integral_value2(0),
	_OP(0),
	_Isum(0),
    _T(coupledValue("T")),
    _C9(coupledValue("C9")),
    _C1(coupledValue("C1")),
    _C0(coupledValue("C0")),
    _C3(coupledValue("C3")),
    _C6(coupledValue("C6")),
    _mesh(_subproblem.mesh().getMesh())
{
}

void
EcorrPost::initialize()
{
  _integral_value = 0;
  _integral_value2 = 0;
  _Isum = 0;
}

void
EcorrPost::execute()
{
	_Isum += computeCurrents();
}

Real
EcorrPost::getValue()
{
  _OP = -0.2;
  std::cout << "Initial Isum" << _Isum << std::endl;
  gatherSum(_Isum);

  Real aaa = _current_side;
  std::cout << "Current node id:" << aaa << std::endl;
  std::cout << "Gather Isum" << _Isum << std::endl;
  Real bbb = _mesh.n_active_elem();
  Real ccc = _mesh.n_elem();
  std::cout << "Active elements number:" << bbb << "Total elements number" << ccc << std::endl;

  for (int i = 0; i < 5; i++)
  {
  if (_Isum > 1E-8)
  {
	  initialize();
	  _OP = _OP - 1E-5;
	  execute();
	  gatherSum(_Isum);
	  std::cout << "Isum" << _Isum << std::endl;
  }
  else if (_Isum < -1E-8)
  {
	  initialize();
	  _OP = _OP + 1E-5;
	  execute();
	  gatherSum(_Isum);
	  std::cout << "Isum" << _Isum << std::endl;
  }
  else
	  break;

  }

  return _Isum;
}

void
EcorrPost::threadJoin(const UserObject & y) //As in the MOOSE index pages, it should not be used! Just use gathersum
{
  const EcorrPost & pps = static_cast<const EcorrPost &>(y);
  _integral_value += pps._integral_value;
  _integral_value2 += pps._integral_value2;
  _Isum += pps._Isum;
}

Real
EcorrPost::computeIntegral()
{
	return 0;
}

Real
EcorrPost::computeCurrents()
{
  Real sum = 0;
    for (_qp = 0; _qp < _qrule->n_points(); _qp++)
    {
      sum = _C1[_qp] + _C0[_qp] + _C3[_qp] + _C6[_qp] + _C9[_qp] + sum;
    }
  return sum;
}

