virtual Real computeUpdatedJ2stress(GenericRankTwoTensor<is_ad>& stress_new);
template <bool is_ad>
Real
creep2Templ<is_ad>::computeUpdatedJ2stress(GenericRankTwoTensor<is_ad>& stress_new)

{
    RankTwoTensor deviator_stress = stress_new.deviatoric();
// compute the J2 stress
  Real dev_stress_squared =
      deviator_stress.doubleContraction(deviator_stress);

  return  dev_stress_squared/2;
