InputParameters
TwoChannelsModel::validParams()
{
  InputParameters params = Component::validParams();
  params.addRequiredParam<std::string>("current_flow_channel",
                                       "Name of flow channel component to connect to");
  params.addRequiredParam<std::string>("adjacent_flow_channel",
                                       "Name of flow channel component to connect to");
  params.addRequiredParam<UserObjectName>(
                    "fp", "The name of the user object that defines fluid properties");
  params.addClassDescription("Calculate the lateral flow between 2 1-phase flow channels");

  return params;
}

TwoChannelsModel::TwoChannelsModel(
    const InputParameters & parameters)
  : Component(parameters),
    _current_flow_channel_name(getParam<std::string>("current_flow_channel")),
    _adjacent_flow_channel_name(getParam<std::string>("adjacent_flow_channel")),
    _fe_type(_sim.getFlowFEType()),
    _fp_name(getParam<UserObjectName>("fp")),
    _comp_name(name()),
    _fch_alignment(_mesh)
{
}

void
TwoChannelsModel::setupMesh()
{
  if (hasComponentByName<FlowChannel1Phase>(_current_flow_channel_name) &&
      hasComponentByName<FlowChannel1Phase>(_adjacent_flow_channel_name))
  {
    const FlowChannel1Phase & current_flow_channel = getComponentByName<FlowChannel1Phase>(_current_flow_channel_name);
    const FlowChannel1Phase & adjacent_flow_channel =
        getComponentByName<FlowChannel1Phase>(_adjacent_flow_channel_name);

    _fch_alignment.build(current_flow_channel.getElementIDs(),adjacent_flow_channel.getElementIDs());

    for (auto & elem_id : adjacent_flow_channel.getElementIDs())
    {
      dof_id_type nearest_elem_id = _fch_alignment.getNearestElemID(elem_id);
      if (nearest_elem_id != DofObject::invalid_id)
        _sim.augmentSparsity(elem_id, nearest_elem_id);
    }
  }
}

void
TwoChannelsModel::check() const
{
  Component::check();

  if (hasComponentByName<FlowChannel1Phase>(_current_flow_channel_name) &&
      hasComponentByName<FlowChannel1Phase>(_adjacent_flow_channel_name))
  {
    const FlowChannel1Phase & current_flow_channel = getComponentByName<FlowChannel1Phase>(_current_flow_channel_name);
    const FlowChannel1Phase & adjacent_flow_channel =
        getComponentByName<FlowChannel1Phase>(_adjacent_flow_channel_name);

    if (current_flow_channel.getNumElems() != adjacent_flow_channel.getNumElems())
    {
      logError("The number of elements in component '",
               _current_flow_channel_name,
               "' is ",
               current_flow_channel.getNumElems(),
               ", but the number of axial elements in component '",
               _adjacent_flow_channel_name,
               "' is ",
               adjacent_flow_channel.getNumElems(),
               ". They must be the same.");
    }
    if (current_flow_channel.getLength() != adjacent_flow_channel.getLength())
    {
      logError("The length of component '",
               _current_flow_channel_name,
               "' is ",
               current_flow_channel.getLength(),
               ", but the length of component '",
               _adjacent_flow_channel_name,
               "' is ",
               adjacent_flow_channel.getLength(),
               ". They must be the same.");
    }
      if (!_fch_alignment.check(adjacent_flow_channel.getElementIDs()))
      {
        logError("The centers of the elements of adjacent flow channel '",
                 _adjacent_flow_channel_name,
                 "' do not align with the centers of the current flow channel.");
       }
  }
}

