RealVectorValue
NisosINSMomentumThermocapillary::Thermocapillary()
{
  RealVectorValue Tc(0, 0, 0);
  Real _im = _integrate_Ma ? 1.0 : 0.0;
  Real _ic = _integrate_TC ? 1.0 : 0.0;
  Tc = -6*_xi*( _im * ( (_grad_c[_qp]*_grad_c[_qp])*_dCoefdT[_qp]*_grad_T[_qp] - _dCoefdT[_qp]*(_grad_T[_qp]*_grad_c[_qp])*_grad_c[_qp] ) ) - _ic*_w[_qp]*_grad_c[_qp] ;
  return Tc;
}



Real
NisosINSMomentumThermocapillary::computeQpResidual()
{
  Real r = 0;
  Real _id = _interpolator ? _idv[_qp] : 1.0;
  // Surface tension term  
  r += _id * _test[_i][_qp] * Thermocapillary()(_component);

  if (_supg)
    r += computeQpPGResidual();

  return r;
}

Real
NisosINSMomentumThermocapillary::computeQpPGResidual()
{
  RealVectorValue U(_u_vel[_qp], _v_vel[_qp], _w_vel[_qp]);
  Real _id = _interpolator ? _idv[_qp] : 1.0;
  return  _id * tau() * U * _grad_test[_i][_qp] * Thermocapillary()(_component);
}

Real
NisosINSMomentumThermocapillary::computeQpJacobian()
{
  Real jac=0;
  if (_supg)
    jac += computeQpPGJacobian(_component);
  return jac;
}

Real
NisosINSMomentumThermocapillary::computeQpPGJacobian(unsigned comp)
{
  Real _id = _interpolator ? _idv[_qp] : 1.0;
  RealVectorValue U(_u_vel[_qp], _v_vel[_qp], _w_vel[_qp]);
  RealVectorValue d_U_d_U_comp(0, 0, 0);
  d_U_d_U_comp(comp) = _phi[_j][_qp];

  return dTauDUComp(comp) * U * _grad_test[_i][_qp] * _id *
             Thermocapillary()(_component) +
         tau() * d_U_d_U_comp * _grad_test[_i][_qp] * _id *
             Thermocapillary()(_component) ;
}



Real
NisosINSMomentumThermocapillary::computeQpOffDiagJacobian(unsigned jvar)
{
  Real jac = 0;
  RealVectorValue U(_u_vel[_qp], _v_vel[_qp], _w_vel[_qp]);
  Real _id = _interpolator ? _idv[_qp] : 1.0;
  Real _im = _integrate_Ma ? 1.0 : 0.0;
  Real _ic = _integrate_TC ? 1.0 : 0.0;
  if (jvar == _u_vel_var_number)
  {
    if (_supg)
      jac += computeQpPGJacobian(0);

    return jac;
  }
  else if (jvar == _v_vel_var_number)
  {
    if (_supg)
      jac += computeQpPGJacobian(1);

    return jac;
  }
  else if (jvar == _w_vel_var_number)
  {
    if (_supg)
      jac += computeQpPGJacobian(2);

    return jac;
  }

  else if (jvar == _w_var)
  {
    jac += -_id * _test[_i][_qp] *_ic * _phi[_j][_qp]*_grad_c[_qp](_component) ;

    if (_supg)

      jac += -_id *  tau() *  U * _grad_test[_i][_qp] * _ic * _phi[_j][_qp] * _grad_c[_qp](_component) ;

