OK, I will introduce the whole stragedy to you. I refer to the stragedy which HeatTransferFromHeatStructure1Phase uses to calculate the heat flux between the boundary of 2D mesh(solid) and the 1D mesh(fluid).
First, I write the mapping between two 1D mesh, which call ChannelMapping(refer to FlowChannelAlignment). Then, I write a UO, which matches the qp point of every element of two 1D mesh(refer to ADHeatFluxFromHeatStructureBaseUserObject). The UO include ChannelMapping.h and call the material properties calculated by VariableValueTransferMaterial. The UO code is here:
#include "ADVariableTransferFromAdjacentChannelUO.h"
#include "MooseMesh.h"
#include "KDTree.h"
#include "Assembly.h"

registerMooseObject("ThermalHydraulicsApp", ADVariableTransferFromAdjacentChannelUO);

InputParameters
ADVariableTransferFromAdjacentChannelUO::validParams()
{
  InputParameters params = ElementUserObject::validParams();
  params.addRequiredParam<ChannelMapping *>("_channel_mapping",
                                                  "Flow channel alignement object");
//  params.addRequiredParam<MaterialPropertyName>("adjacent_a", "Area of adjacent channel");
  params.addRequiredParam<MaterialPropertyName>("adjacent_rhoa", "Density*Area of adjacent channel");
  params.addRequiredParam<MaterialPropertyName>("adjacent_rhoua", "Density*Velocity*Area of adjacent channel");
  params.addRequiredParam<MaterialPropertyName>("adjacent_rhoea", "Density*Energy*Area of adjacent channel");
  params.addRequiredParam<MaterialPropertyName>("adjacent_gradrhoa", "Gradient of Density*Area of adjacent channel");
  params.addRequiredParam<MaterialPropertyName>("adjacent_gradrhoua", "Gradient of Density*Velocity*Area of adjacent channel");

//  params.addRequiredCoupledVar("P_hf", "Heat flux perimeter");
  params.addClassDescription(
      "Base class for caching variables between 2 flow channels.");
  return params;
}

ADVariableTransferFromAdjacentChannelUO::ADVariableTransferFromAdjacentChannelUO(
    const InputParameters & parameters)
  : ElementUserObject(parameters),
    _channel_mapping(*getParam<ChannelMapping *>("_channel_mapping")),
//    _adjacent_a(getADMaterialProperty<Real>("adjacent_a")),
    _adjacent_rhoa(getADMaterialProperty<Real>("adjacent_rhoa")),
    _adjacent_rhoua(getADMaterialProperty<Real>("adjacent_rhoua")),
    _adjacent_rhoea(getADMaterialProperty<Real>("adjacent_rhoea")),
    _adjacent_gradrhoa(getADMaterialProperty<RealVectorValue>("adjacent_gradrhoa")),
    _adjacent_gradrhoua(getADMaterialProperty<RealVectorValue>("adjacent_gradrhoua"))

  // _P_hf(adCoupledValue("P_hf"))
{
//  const auto & master_boundary_info = _channel_mapping.getMasterBoundaryInfo();
  const auto & master_elem_ids = _channel_mapping.getMasterElementIDs();
  const auto & slave_elem_ids = _channel_mapping.getSlaveElementIDs();

  // list of q-points per master elements
  std::map<dof_id_type, std::vector<Point>> master_elem_qps;
  // list of q-points per slave elements
  std::map<dof_id_type, std::vector<Point>> slave_elem_qps;

//  for (const auto & t : master_boundary_info)
//  {
//    auto elem_id = std::get<0>(t);
//    auto side_id = std::get<1>(t);
//    const Elem * elem = _mesh.elemPtr(elem_id);
//    if (elem->processor_id() == _subproblem.processor_id())
//    {
      // 2D elements
//      _assembly.setCurrentSubdomainID(elem->subdomain_id());
//      _assembly.reinit(elem, side_id);
//      const MooseArray<Point> & q_points = _assembly.qPointsFace();
//      for (std::size_t i = 0; i < q_points.size(); i++)
//        master_elem_qps[elem_id].push_back(q_points[i]);
//    }
//  }

// master flow channel
  for (const auto & elem_id : master_elem_ids)
  {
    const Elem * elem = _mesh.elemPtr(elem_id);
    if (elem->processor_id() == _subproblem.processor_id())
    {
      // 1D elements
      _assembly.setCurrentSubdomainID(elem->subdomain_id());
      _assembly.reinit(elem);
      const MooseArray<Point> & q_points = _assembly.qPoints();
      for (std::size_t i = 0; i < q_points.size(); i++)
        master_elem_qps[elem_id].push_back(q_points[i]);
    }
  }

// slave flow channel
  for (const auto & elem_id : slave_elem_ids)
  {
    const Elem * elem = _mesh.elemPtr(elem_id);
    if (elem->processor_id() == _subproblem.processor_id())
    {
      // 1D elements
      _assembly.setCurrentSubdomainID(elem->subdomain_id());
      _assembly.reinit(elem);
      const MooseArray<Point> & q_points = _assembly.qPoints();
      for (std::size_t i = 0; i < q_points.size(); i++)
        slave_elem_qps[elem_id].push_back(q_points[i]);
    }
  }

  // now find out how q-points correspond to each other on the (master, slave) pair of elements
  for (auto elem_id : slave_elem_ids)
  {
    dof_id_type nearest_elem_id = _channel_mapping.getNearestElemID(elem_id);

    std::vector<Point> & slave_qps = slave_elem_qps[elem_id];
    std::vector<Point> & master_qps = master_elem_qps[nearest_elem_id];
    mooseAssert(slave_qps.size() == master_qps.size(),
                "Number of master and slave q-points have to match");
    _elem_qp_map[elem_id].resize(slave_qps.size());
    KDTree kd_tree_qp(master_qps, 5);
    for (std::size_t i = 0; i < slave_qps.size(); i++)
    {
      unsigned int patch_size = 1;
      std::vector<std::size_t> return_index(patch_size);
      kd_tree_qp.neighborSearch(slave_qps[i], patch_size, return_index);
      _elem_qp_map[elem_id][i] = return_index[0];
    }
  }
}

