    _adj_rhoea[_current_elem->id()][_qp] = _adjacent_rhoea[_qp];
    _adj_rhoea[nearest_elem_id][nearest_qp] = _adjacent_rhoea[_qp];

    _adj_gradrhoa[_current_elem->id()][_qp] = _adjacent_gradrhoa[_qp];
    _adj_gradrhoa[nearest_elem_id][nearest_qp] = _adjacent_gradrhoa[_qp];

    _adj_gradrhoua[_current_elem->id()][_qp] = _adjacent_gradrhoua[_qp];
    _adj_gradrhoua[nearest_elem_id][nearest_qp] = _adjacent_gradrhoua[_qp];
