void
TwoChannelsModel::check() const
{
  Component::check();

  if (hasComponentByName<FlowChannel1Phase>(_current_flow_channel_name) &&
      hasComponentByName<FlowChannel1Phase>(_adjacent_flow_channel_name))
  {
    const FlowChannel1Phase & current_flow_channel = getComponentByName<FlowChannel1Phase>(_current_flow_channel_name);
    const FlowChannel1Phase & adjacent_flow_channel =
        getComponentByName<FlowChannel1Phase>(_adjacent_flow_channel_name);

    if (current_flow_channel.getNumElems() != adjacent_flow_channel.getNumElems())
    {
      logError("The number of elements in component '",
               _current_flow_channel_name,
               "' is ",
               current_flow_channel.getNumElems(),
               ", but the number of axial elements in component '",
               _adjacent_flow_channel_name,
               "' is ",
               adjacent_flow_channel.getNumElems(),
               ". They must be the same.");
    }
    if (current_flow_channel.getLength() != adjacent_flow_channel.getLength())
    {
      logError("The length of component '",
               _current_flow_channel_name,
               "' is ",
               current_flow_channel.getLength(),
               ", but the length of component '",
               _adjacent_flow_channel_name,
               "' is ",
               adjacent_flow_channel.getLength(),
               ". They must be the same.");
    }
      if (!_fch_alignment.check(adjacent_flow_channel.getElementIDs()))
      {
        logError("The centers of the elements of adjacent flow channel '",
                 _adjacent_flow_channel_name,
                 "' do not align with the centers of the current flow channel.");
       }
  }
}

