I tried modifying the code for  GapConductance
void
GasGapConductance::computeQpConductance()
{
  if (_has_info)
  {
    const ADReal cache_conductance= h_conduction() + h_radiation()+h_contact();
    // _gap_conductance[_qp] = h_conduction() + h_radiation();
     _gap_conductance[_qp] = cache_conductance.value();
    // _gap_conductance_dT[_qp] = dh_conduction() + dh_radiation();
    _gap_conductance_dT[_qp] =cache_conductance.derivatives();
  }
  else
  {
    _gap_conductance[_qp] = 0;
    _gap_conductance_dT[_qp] = 0;
  }
