I have a problem in calculating the residual correctly. Here is my .h and .C file.
#pragma once

#include "Kernel.h"

class SurfaceTension : public Kernel
{
public:
  SurfaceTension(const InputParameters & parameters);

  static InputParameters validParams();

protected:
  virtual Real computeQpResidual() override;

  virtual Real computeQpJacobian() override;

private:
  unsigned int _v_var;
  const VariableGradient & _grad_c;
  const VariableValue & _w;
  Real _coef;
};


#include "SurfaceTension.h"

registerMooseObject("ExampleApp", SurfaceTension);

InputParameters
SurfaceTension::validParams()
{
  InputParameters params = Kernel::validParams();

  params.addRequiredCoupledVar(
      "w", "The gradient of this variable will be used.");
    params.addRequiredCoupledVar(
      "c", "The value of this variable will be used.");
      params.addParam<Real>(
      "coef", 1.0, "Coefficent ($\\sigma$) multiplier for the coupled force term.");


  return params;
}

SurfaceTension::SurfaceTension(const InputParameters & parameters)
  : Kernel(parameters),

    _v_var(coupled("v")),
    _grad_c(coupledGradient("c")),
    _w(coupledValue("w"))
{
}

Real
SurfaceTension::computeQpResidual()
{

  return _test[_i][_qp] * (_grad_c[_qp] * _w[_qp]) * _coef;

}

Real
SurfaceTension::computeQpJacobian()
{

  return _test[_i][_qp] * (_grad_c[_qp] * _w[_qp] * _grad_phi[_j][_qp]) * _coef;
}

