Hello,
Here I inherited ComputeEigenstrainBase for eigenstrain model and applied it to the input file. But I found that I did not seem to have succeeded in applying this model to my simulations. I want to know why the file I wrote is not being called.
Here is the .h and .C,
#pragma once

#include "ComputeEigenstrainBase.h"

#include "RankTwoTensor.h"
#include "Function.h"

class Function;
template <bool is_ad>
class ComputeIrridiationTempl : public ComputeEigenstrainBaseTempl<is_ad>
{
public:
  static InputParameters validParams();

  ComputeIrridiationTempl(const InputParameters & parameters);

protected:

  virtual void computeQpEigenstrain() override;
  virtual void computeQpProperties() override;

  const Function & _neu;
  const GenericMaterialProperty<Real, is_ad> & _prefactor;

  GenericMaterialProperty<Real, is_ad> & _strain_glc;
  GenericMaterialProperty<Real, is_ad> & _strain_grc;
  GenericMaterialProperty<Real, is_ad> & _strain_cir;
  GenericMaterialProperty<RankTwoTensor, is_ad> & _eigen_base_tensor;

  using Material::_qp;
  using Material::_t;
  using Material::_q_point;
  using ComputeEigenstrainBaseTempl<is_ad>::_eigenstrain;
};

typedef ComputeIrridiationTempl<false> ComputeIrridiation;
typedef ComputeIrridiationTempl<true> ADComputeIrridiation;

#include "ComputeIrridiation.h"

registerMooseObject("WorkerApp", ComputeIrridiation);
registerMooseObject("WorkerApp", ADComputeIrridiation);

template <bool is_ad>
InputParameters
ComputeIrridiationTempl<is_ad>::validParams()
{
  InputParameters params = ComputeEigenstrainBase::validParams();
  params.addClassDescription("Computes a constant Eigenstrain");
  params.addParam<FunctionName>("neu", 0.0, "Neutron");
  params.addParam<MaterialPropertyName>(
      "prefactor", 1.0, "Name of material property defining the variable dependence");
  return params;
}

template <bool is_ad>
ComputeIrridiationTempl<is_ad>::ComputeIrridiationTempl(const InputParameters & parameters)
  : ComputeEigenstrainBaseTempl<is_ad>(parameters),
    _prefactor(this->template getGenericMaterialProperty<Real, is_ad>("prefactor")),
    _strain_glc(this->template declareGenericProperty<Real, is_ad>("strain_glc")),
    _strain_grc(this->template declareGenericProperty<Real, is_ad>("strain_grc")),
    _strain_cir(this->template declareGenericProperty<Real, is_ad>("strain_cir")),
    _eigen_base_tensor(this->template declareGenericProperty<RankTwoTensor, is_ad>("eigen_base_tensor")),
    _neu(this->getFunction("neu"))
{

}

template <bool is_ad>
void
ComputeIrridiationTempl<is_ad>::computeQpProperties()
{
  _strain_glc[_qp] = 2.18e-21 * std::pow(_neu.value(_t, _q_point[_qp]) * _t / 10000., 0.845);
  //_strain_glc[_qp] = 0.1;
  _strain_grc[_qp] = std::pow((1. + _strain_glc[_qp]), -0.5) - 1;
  _strain_cir[_qp] = 0.0;
  _eigen_base_tensor[_qp].fillFromInputVector({_strain_grc[_qp],_strain_glc[_qp],_strain_cir[_qp]});
}



template <bool is_ad>
void
ComputeIrridiationTempl<is_ad>::computeQpEigenstrain()
{
  // Define Eigenstrain
  _eigenstrain[_qp] = _eigen_base_tensor[_qp] * _prefactor[_qp];
}

