#include "ComputeGeneralStressBaseTest.h"
#include "ComputeElasticityTensorBase.h"
#include "Function.h"

InputParameters
ComputeGeneralStressBaseTest::validParams()
{
  InputParameters params = Material::validParams();
  params.addParam<std::string>("base_name",
                               "Optional parameter that allows the user to define "
                               "multiple mechanics material systems on the same "
                               "block, i.e. for multiple phases");
  return params;
}

ComputeGeneralStressBaseTest::ComputeGeneralStressBaseTest(const InputParameters & parameters)
  : DerivativeMaterialInterface<Material>(parameters),
    _base_name(isParamValid("base_name") ? getParam<std::string>("base_name") + "_" : ""),
    _mechanical_strain(getMaterialPropertyByName<RankTwoTensor>(_base_name + "mechanical_strain")),
    _stress(declareProperty<RankTwoTensor>(_base_name + "stress")),
    _elastic_strain(declareProperty<RankTwoTensor>(_base_name + "elastic_strain")),
    _extra_stress(getDefaultMaterialProperty<RankTwoTensor>(_base_name + "extra_stress")),
    _Jacobian_mult(declareProperty<RankFourTensor>(_base_name + "Jacobian_mult")),
    _additional_prop(declareProperty<Real>("additional_prop"))
{
}

void
ComputeGeneralStressBaseTest::initQpStatefulProperties()
{
  _elastic_strain[_qp].zero();
  _stress[_qp].zero();

  _additional_prop[_qp] = 0.0;
}

void
ComputeGeneralStressBaseTest::computeQpProperties()
{
  computeQpStress();

  // Add in extra stress
  _stress[_qp] += _extra_stress[_qp];
}

Then in the ComputeLinearElasticStress.C, I did three steps: 1. retrieve the old value, 2. update value (by adding 1), 3. assign back value.
#include "ComputeLinearElasticStressTest.h"

registerMooseObject("farms_cbdmApp", ComputeLinearElasticStressTest);

InputParameters
ComputeLinearElasticStressTest::validParams()
{
  InputParameters params = ComputeStressBaseTest::validParams();
  params.addClassDescription("Compute stress using elasticity for small strains");
  return params;
}

ComputeLinearElasticStressTest::ComputeLinearElasticStressTest(const InputParameters & parameters)
  : ComputeStressBaseTest(parameters),
    _elasticity_tensor_name(_base_name + "elasticity_tensor"),
    _elasticity_tensor(getMaterialPropertyByName<RankFourTensor>(_elasticity_tensor_name))
{
}

void
ComputeLinearElasticStressTest::initialSetup()
{
  // _base_name + "unstabilized_deformation_gradient" is only declared if we're
  // using the Lagrangian kernels.  It's okay to invoke this small strain
  // material if you are using that kernel system and the
  // ComputeLagrangianWrappedStress wrapper
  if (hasBlockMaterialProperty<RankTwoTensor>(_base_name + "strain_increment") &&
      !hasBlockMaterialProperty<RankTwoTensor>(_base_name + "unstabilized_deformation_gradient"))
    mooseError("This linear elastic stress calculation only works for small strains; use "
               "ComputeFiniteStrainElasticStress for simulations using incremental and finite "
               "strains.");
}

void
ComputeLinearElasticStressTest::computeQpStress()
{
  // stress = C * e
  _stress[_qp] = _elasticity_tensor[_qp] * _mechanical_strain[_qp];

  // Assign value for elastic strain, which is equal to the mechanical strain
  _elastic_strain[_qp] = _mechanical_strain[_qp];

  // Compute dstress_dstrain
  _Jacobian_mult[_qp] = _elasticity_tensor[_qp];

  //add 1 to the props
  //retrieve value
  Real additional_prop_vals = _additional_prop[_qp];
  //do some calculations
  Real additional_prop_vals_add_1 = additional_prop_vals + 1.0;
  //update value
  _additional_prop[_qp] = additional_prop_vals_add_1;

  Real x_coord = _q_point[_qp](0);
  Real y_coord = _q_point[_qp](1);
  std::cout<<" x_coord: "<<x_coord<<" y_coord: "<<y_coord<<" additional_prop_vals: "<<additional_prop_vals<<" _additional_prop[_qp]: "<<_additional_prop[_qp]<<std::endl;
}

