PorousFlowCapillaryPressureVGBE::PorousFlowCapillaryPressureVGBE(const InputParameters & parameters)
  : PorousFlowCapillaryPressure(parameters), 
    _m(getMaterialProperty<Real>("mm")),   *take m in as MaterialProperty
