Hi @lynnmunday, so I did a couple of tests to check it out--
For a block geometry with a single element both methods (fillmethod orthotropic and symmetric9) gave identical results under uniaxial loading with expected stresses.
Then I added a temperature field in the block and the two methods gave different results (my Young's modulus and alpha_CTE were not temperature dependent).

following is my code snippet where I directly calculate the orthotropic tensor from 12 elastic constants using fill method orthotropic
#include "ComputeVariableOrthotropicElasticityTensor.h"
#include "RotationTensor.h"

registerMooseObject("TensorMechanicsApp", ComputeVariableOrthotropicElasticityTensor);
registerMooseObject("TensorMechanicsApp", ADComputeVariableOrthotropicElasticityTensor);

template <bool is_ad>
InputParameters
ComputeVariableOrthotropicElasticityTensorTempl<is_ad>::validParams()
{
  InputParameters params = ComputeRotatedElasticityTensorBaseTempl<is_ad>::validParams();
  params.addClassDescription("Compute an orthotropic elasticity tensor for elastic constants that "
                             "change as a function of temperature");

  params.addRequiredCoupledVar("temperature", "Coupled temperature");

  params.addRequiredParam<FunctionName>(
      "E11",
      "Function describing the Young's modulus E11");
  params.addRequiredParam<FunctionName>(
      "E22",
      "Function describing the Young's modulus E22");
  params.addRequiredParam<FunctionName>(
      "E33",
      "Function describing the Young's modulus E33");

  params.addRequiredParam<FunctionName>(
      "G12",
      "Function describing the shear modulus G12");
  params.addRequiredParam<FunctionName>(
      "G23",
      "Function describing the shear modulus G23");
  params.addRequiredParam<FunctionName>(
      "G31",
      "Function describing the shear modulus G31");

  params.addRequiredParam<FunctionName>(
      "nu12",
      "Function describing the Poisson's ratio nu12");
  params.addRequiredParam<FunctionName>(
      "nu23",
      "Function describing the Poisson's ratio nu23");
  params.addRequiredParam<FunctionName>(
      "nu13",
      "Function describing the Poisson's ratio nu13");

  return params;
}


template <bool is_ad>
ComputeVariableOrthotropicElasticityTensorTempl<is_ad>::ComputeVariableOrthotropicElasticityTensorTempl(
    const InputParameters & parameters)
  : ComputeRotatedElasticityTensorBaseTempl<is_ad>(parameters),
  _temperature(this->coupledValue("temperature")),
  _temp_name(this->getVar("temperature",0)->name()),
  _E11(this->getFunction("E11")),
  _E22(this->getFunction("E22")),
  _E33(this->getFunction("E33")),
  _G12(this->getFunction("G12")),
  _G23(this->getFunction("G23")),
  _G31(this->getFunction("G31")),
  _nu12(this->getFunction("nu12")),
  _nu23(this->getFunction("nu23")),
  _nu13(this->getFunction("nu13")),
  _delasticity_tensor_dT(
  this->template declarePropertyDerivative<RankFourTensor>(_elasticity_tensor_name, _temp_name) )
{ 
}

template <bool is_ad>
void
ComputeVariableOrthotropicElasticityTensorTempl<is_ad>::computeQpElasticityTensor()
{
  // Get the stiffness values for the current temperature
  current_qp_temperature = 0.0;
  current_qp_temperature = _temperature[_qp];
  _ortho_stiffness_components.resize(12);

  // this should be the order: E11, E22 E33 G12 G23 G31/G13 nu21 nu31 nu32 nu12 nu13 nu23
  // Ansys takes E11 E22 E33 nu12 nu 23 nu13 G12 G23 G13/G31
  E11 = 0.0; E22 = 0.0; E33 = 0.0;
  G12 = 0.0; G23 = 0.0; G31 = 0.0;
  nu12 = 0.0; nu13 = 0.0; nu23 = 0.0;
  nu21 = 0.0; nu31 = 0.0; nu32 = 0.0;

  E11 = _E11.value(current_qp_temperature) * 1e-8 ;
  E22 = _E22.value(current_qp_temperature) * 1e-8 ;
  E33 = _E33.value(current_qp_temperature) * 1e-8 ;

  G12 = _G12.value(current_qp_temperature) * 1e-8 ;
  G23 = _G23.value(current_qp_temperature) * 1e-8 ;
  G31 = _G31.value(current_qp_temperature) * 1e-8 ;

  nu12 = _nu12.value(current_qp_temperature);
  nu13 = _nu13.value(current_qp_temperature);
  nu23 = _nu23.value(current_qp_temperature);

  // _nu21 = _nu12 * (_E22/_E11) ; 
  nu21 = (nu12 * E22) / E11 ;
  // _nu31 = _nu13 * (_E33/_E11) ;   
  nu31 = (nu13 * E33) / E11 ;
  // _nu32 = _nu23 * (_E33/_E22) ; 
  nu32 = (nu23 * E33) / E22 ;


  _ortho_stiffness_components[0]  = E11 ;  
  _ortho_stiffness_components[1]  = E22 ;  
  _ortho_stiffness_components[2]  = E33 ;  
  _ortho_stiffness_components[3]  = G12 ;  
  _ortho_stiffness_components[4]  = G23 ;  
  _ortho_stiffness_components[5]  = G31 ;  
  _ortho_stiffness_components[6]  = nu21 ;  
  _ortho_stiffness_components[7]  = nu31 ;  
  _ortho_stiffness_components[8]  = nu32 ;  
  _ortho_stiffness_components[9]   = nu12 ;  
  _ortho_stiffness_components[10]  = nu13 ;  
  _ortho_stiffness_components[11]  = nu23 ;  


  _ortho_Cijkl.zero();
  _ortho_Cijkl.fillFromInputVector(_ortho_stiffness_components, RankFourTensor:: orthotropic);
  // Assign elasticity tensor at a given quad point
  _elasticity_tensor[_qp] = _ortho_Cijkl *1e8;

  _dortho_Cijkl.zero();
 _delasticity_tensor_dT[_qp] = _dortho_Cijkl;


}

