template <bool is_ad>
void
ComputeVariableAnisotropicElasticityTensorTempl<is_ad>::computeQpElasticityTensor()
{
  // Get the stiffness values for the current temperature
  current_qp_temperature = 0.0;
  current_qp_temperature = _temperature[_qp];
  _ortho_stiffness_components.resize(9);

  // this should be the order: E11, E22 E33 G12 G23 G31/G13 nu21 nu31 nu32 nu12 nu13 nu23
  // Ansys takes E11 E22 E33 nu12 nu 23 nu13 G12 G23 G13/G31
  E11  = 0.0; E22  = 0.0; E33  = 0.0;
  G12  = 0.0; G23  = 0.0; G31  = 0.0;
  nu12 = 0.0; nu13 = 0.0; nu23 = 0.0;
  nu21 = 0.0; nu31 = 0.0; nu32 = 0.0;

  E11 = _E11.value(current_qp_temperature) ;
  E22 = _E22.value(current_qp_temperature) ;
  E33 = _E33.value(current_qp_temperature) ;

  G12 = _G12.value(current_qp_temperature) ;
  G23 = _G23.value(current_qp_temperature) ;
  G31 = _G31.value(current_qp_temperature) ;

  nu12 = _nu12.value(current_qp_temperature);
  nu13 = _nu13.value(current_qp_temperature);
  nu23 = _nu23.value(current_qp_temperature);

  // _nu21 = _nu12 * (_E22/_E11) ; 
  nu21 = (nu12 * E22) / E11 ;
  // _nu31 = _nu13 * (_E33/_E11) ;   
  nu31 = (nu13 * E33) / E11 ;
  // _nu32 = _nu23 * (_E33/_E22) ; 
  nu32 = (nu23 * E33) / E22 ;

  //  k = 1 - nu12nu21 - nu23nu32 - nu13nu31 - nu12nu23nu31 - nu21nu32nu13 ;  
  k     = 1 - nu12*nu21 - nu23*nu32 - nu13*nu31 - nu12*nu23*nu31 - nu21*nu32*nu13 ;  

  // C11 = E11 * (1 - nu23nu32)/k
  C11 = E11 * ( 1 - nu23*nu32) / k ;

  // C22 = E22 * (1 - nu13nu31)/k
  C22 = E22 * ( 1 - nu13*nu31) / k ;

  // C33 = E33 * (1 - nu12nu21)/k
  C33 = E33 * ( 1 - nu12*nu21) / k ;

  // C12 = E11 * (nu23nu31 + nu21) /k
  C12 = E11 * (nu23*nu31 + nu21) / k ;

  // C13 = E11 * (nu21nu32 + nu31) /k
  C13 = E11 * (nu21*nu32 + nu31) / k ;

  // C23 = E22 * (nu12nu31 + nu32) / k
  C23 = E22 * (nu12*nu31 + nu32) / k ;

  // C44 = G12
  C44 = G12 ;

  // C55 = G31
  C55 = G31 ;

  // C66 = G23
  C66 = G23 ;



 // C_ijkl = 'C11 C12 C13 C22 C23 C33 C44 C55 C66'
  _ortho_stiffness_components[0]  = C11 ;  
  _ortho_stiffness_components[1]  = C12 ;  
  _ortho_stiffness_components[2]  = C13 ;  
  _ortho_stiffness_components[3]  = C22 ;  
  _ortho_stiffness_components[4]  = C23 ;  
  _ortho_stiffness_components[5]  = C33 ;  
  _ortho_stiffness_components[6]  = C44 ;  
  _ortho_stiffness_components[7]  = C55 ;  
  _ortho_stiffness_components[8]  = C66 ;  

  _ortho_Cijkl.zero();

  _ortho_Cijkl.fillFromInputVector(_ortho_stiffness_components, RankFourTensor:: symmetric9);
  // Assign elasticity tensor at a given quad point
  _elasticity_tensor[_qp] = _ortho_Cijkl;

 _dortho_Cijkl.zero();
 _delasticity_tensor_dT[_qp] = _dortho_Cijkl;


}

