#include "PorousFlowThermalConductivityBrakelmann.h"

registerMooseObject("PorousFlowApp", PorousFlowThermalConductivityBrakelmann);

InputParameters
PorousFlowThermalConductivityBrakelmann::validParams()
{
  InputParameters params = PorousFlowThermalConductivityBase::validParams();
  params.addRequiredParam<Real>("lambda_s",
                             "The thermal conductivity of the porous medium");
  params.addRequiredParam<Real>("lambda_w",
                             "The thermal conductivity of water");
  params.addParam<unsigned>("aqueous_phase_number",
                             0,
                             "The phase number of the aqueous phase.");
  params.addClassDescription("This Material calculates rock-fluid combined thermal conductivity "
                             "for the unsaturated porous medium using a empirical"
                             "non-linear whole-range function from Brakelmann (2015)."
                             "Thermal conductivity = lambda_w^(phi)*lambda_b^(1-phi)*exp(-3.08*phi*(1-S)^2)) "
                             "where phi is porosity, lambda_w, lambda_b are "
                             "thermal conductivities of the fluid and solid"
                             "(approximated from grain size distribution)"
                              "and S is the degree of saturation. This Material requires the thermal conductivity of"
                              "water from the GeneralFluidProps object and the thermal conductivity of soil from a rank_two_tensor"
                            );
  return params;
}

PorousFlowThermalConductivityBrakelmann::PorousFlowThermalConductivityBrakelmann(
    const InputParameters & parameters)
  : PorousFlowThermalConductivityBase(parameters),
    _la_b(getParam<RealTensorValue>("lambda_s")),
    _la_w(getParam<Real>("lambda_w")),
    _porosity_qp(getMaterialProperty<Real>("PorousFlow_porosity_qp")),
    _dporosity_qp_dvar(getMaterialProperty<std::vector<Real>>("dPorousFlow_porosity_qp_dvar")),
    _aqueous_phase(_num_phases > 0),
    _aqueous_phase_number(getParam<unsigned>("aqueous_phase_number")),
    _saturation_qp(_aqueous_phase
                       ? &getMaterialProperty<std::vector<Real>>("PorousFlow_saturation_qp")
                       : nullptr),
    _dsaturation_qp_dvar(_aqueous_phase ? &getMaterialProperty<std::vector<std::vector<Real>>>(
                                              "dPorousFlow_saturation_qp_dvar")
                                        : nullptr)
{
  if (_num_phases != 1)
    paramError("fluid_phase",
               "The Dictator proclaims that the number of phases is ",
               _dictator.numPhases(),
               " whereas this material can only be used for single phase "
               "simulations.  Be aware that the Dictator has noted your mistake.");
}

void
PorousFlowThermalConductivityBrakelmann::computeQpProperties()
{
  _la_qp[_qp] =
    std::pow(_la_w, _porosity_qp[_qp]) * std::pow(_la_b, 1-_porosity_qp[_qp]) * std::exp(-3.08*_porosity_qp[_qp] * std::pow((1-((*_saturation_qp)[_qp][_aqueous_phase_number])/_porosity_qp[_qp]),2));

  _dla_qp_dvar[_qp].assign(_num_var, RealTensorValue());
  for (unsigned v = 0; v < _num_var; ++v)
    _dla_qp_dvar[_qp][v] =
      std::pow(_la_w, _porosity_qp[)*std::pow(_la_b, 1-_porosity_qp[_qp])*
      std::exp(-3.08*_porosity_qp[_qp]*std::pow((1-((*_saturation_qp)[_qp][_aqueous_phase_number])/_porosity_qp[_qp]), 2))*(std::log(_la_w)-std::log(_la_b))*_dporosity_qp_dvar[_qp][v]-
      6.16*_porosity_qp[_qp]*(1-((*_saturation_qp)[_qp][_aqueous_phase_number])/_porosity_qp[_qp])*std::pow(_la_w, _porosity_qp[_qp])*std::pow(_la_b, 1-_porosity_qp[_qp])*
      std::exp(-3.08*_porosity_qp[_qp]*std::pow((1-((*_saturation_qp)[_qp][_aqueous_phase_number])/_porosity_qp[_qp]), 2))*(*_dsaturation_qp_dvar)[_qp][_aqueous_phase_number][v];
}

@richmondodufisan Most of what you are wanting to do would be covered by the optimization module:   https://mooseframework.inl.gov/moose/modules/optimization/
We mostly focused on gradient based optimization using the adjoint method.  It can handle transient and steady state problems although transient problems are more difficult.  There are several steady state examples in the test and examples directory of the module.  Here is an example for transient material inversion that hasn't been merged yet:
