In AdjointSolve.C the way we get the jacobian matrix is
 // Adjoint matrix, solution, and right-hand-side
  auto & matrix = static_cast<ImplicitSystem &>(_nl_forward.system()).get_system_matrix();

The Jacobian matrix is only associated with a tag during Jacobian evaluation:
void
NonlinearSystemBase::computeJacobian(SparseMatrix<Number> & jacobian, const std::set<TagID> & tags)
{
  associateMatrixToTag(jacobian, systemMatrixTag());

  computeJacobianTags(tags);

  disassociateMatrixFromTag(jacobian, systemMatrixTag());
}

#pragma once

#include "NodalBC.h"

/// Implements a coupled Dirichlet BC where u^2 +Psi_im^2 = Psi_mag on the boundary.
class SuperBC : public NodalBC
{
public:
  SuperBC(const InputParameters & parameters);

  static InputParameters validParams();

protected:
  virtual Real computeQpResidual() override;

private:
  /// Multiplier on the boundary.
  Real _psi_MAG;
  /// reference to a user-specifiable coupled (independent) variable
  const VariableValue & _Psi_Im;
};

