#include "AdvectiveFluxAuxBE.h"
#include "Assembly.h"

registerMooseObject("moose_behApp", AdvectiveFluxAuxBE);

InputParameters
AdvectiveFluxAuxBE::validParams()
{
  InputParameters params = AuxKernel::validParams();
  MooseEnum component("x y z normal");
  params.addClassDescription("Compute components of flux vector for advection problems "
                             "$(\\vec{J} = \\vec{v} \\cdot \\vec{n})$.");
  params.addCoupledVar("vx", "The x component of velocity");
  params.addCoupledVar("vy", "The y component of velocity");
  params.addCoupledVar("vz", "The z component of velocity");
  params.addParam<MooseEnum>("component", component, "The desired component of flux."); // Added this line
  return params;
}

AdvectiveFluxAuxBE::AdvectiveFluxAuxBE(const InputParameters & parameters)
  : AuxKernel(parameters),
    _component(getParam<MooseEnum>("component")),
    _vx(coupledValue("vx")),
    _vy(coupledValue("vy")),
    _vz(coupledValue("vz")),
    _normals(_assembly.normals()),
    _use_normal(getParam<MooseEnum>("component") == "normal")
{
  if (_use_normal && !isParamValid("boundary"))
    paramError("boundary", "A boundary must be provided if using the normal component!");
}

Real
AdvectiveFluxAuxBE::computeValue()
{
  // RealVectorValue velocity(_v[_qp]);
  return (_vx[_qp] * _normals[_qp](0)) + (_vy[_qp] * _normals[_qp](1)) + ((_vz[_qp]) * _normals[_qp](2)) ;
}

