#pragma once
#include "IntegratedBC.h"
class Function;
class TKFunctionEnvTempCoupledNeumannBC : public IntegratedBC
{
public:
TKFunctionEnvTempCoupledNeumannBC(const InputParameters & parameters);
    static InputParameters validParams();

protected:
    virtual Real computeQpResidual() override;

private:
    Real _lambdaE;

    const Function & _temperatureE;

    const VariableValue & _coupledTemperature_val;

};

#include "TKFunctionEnvTempCoupledNeumannBC.h"
#include "Function.h"
registerMooseObject("PolarBearApp", TKFunctionEnvTempCoupledNeumannBC);
InputParameters
TKFunctionEnvTempCoupledNeumannBC::validParams()
{
InputParameters params = IntegratedBC::validParams();
params.addParam<Real>("lambdaE", 1.0, "value multiplied");
params.addParam<FunctionName>("temperatureE", 273.0, "environment temperature");

params.addRequiredCoupledVar("temperature", "value on the BC");
return params;

}
TKFunctionEnvTempCoupledNeumannBC::TKFunctionEnvTempCoupledNeumannBC(const InputParameters & parameters)
: IntegratedBC(parameters),
// getting the required parameters from the input file
_lambdaE(getParam("lambdaE")),        //The multiplier
_temperatureE(getFunction("temperatureE")),//The environment temperature
_coupledTemperature_val(coupledValue("temperature")) //Coupled variable
{
}
Real
TKFunctionEnvTempCoupledNeumannBC::computeQpResidual()
{
return _test[_i][_qp] * _lambdaE * (_coupledTemperature_val[_qp] - _temperatureE.value(_t, _current_qp[_qp]));
}
