#pragma once

#include "SideVectorPostprocessor.h"
#include "SamplerBase.h"

class SideValueSampler_crab : public SideVectorPostprocessor, protected SamplerBase
{
public:
  static InputParameters validParams();

  SideValueSampler_crab(const InputParameters & parameters);

  virtual void initialize() override;
  virtual void execute() override;
  virtual void finalize() override;

  // Let the SamplerBase version of threadJoin() take part in the
  // overload resolution process, otherwise we get warnings about
  // overloaded virtual functions and "hiding" in debug mode.
  using SamplerBase::threadJoin;

  virtual void threadJoin(const UserObject & y) override;

protected:
  /// So we don't have to create and destroy this vector over and over again
  std::vector<Real> _values;

  unsigned int _qp;
};

@GiudGiud , My source code
#include "SideValueSampler_crab.h"

// MOOSE includes
#include "MooseVariableFE.h"

#include "libmesh/quadrature.h"

registerMooseObject("MooseApp", SideValueSampler_crab);

InputParameters
SideValueSampler_crab::validParams()
{
  InputParameters params = SideVectorPostprocessor::validParams();
  params += SamplerBase::validParams();
  params.addClassDescription("Sample variable(s) along a sideset, internal or external.");
  params.addRequiredCoupledVar(
      "variable", "The names of the variables that this VectorPostprocessor operates on");

  return params;
}

SideValueSampler_crab::SideValueSampler_crab(const InputParameters & parameters)
  : SideVectorPostprocessor(parameters), SamplerBase(parameters, this, _communicator)
{
  std::vector<std::string> var_names(_coupled_moose_vars.size());
  _values.resize(_coupled_moose_vars.size());

  for (unsigned int i = 0; i < _coupled_moose_vars.size(); i++)
    var_names[i] = _coupled_moose_vars[i]->name();

  // Initialize the datastructions in SamplerBase
  SamplerBase::setupVariables(var_names);
}

void
SideValueSampler_crab::initialize()
{
  SamplerBase::initialize();
}

void
SideValueSampler_crab::execute()
{
  for (unsigned int _qp = 0; _qp < _qrule->n_points(); _qp++)
  {
    for (unsigned int i = 0; i < _coupled_moose_vars.size(); i++)
      _values[i] = (dynamic_cast<MooseVariable *>(_coupled_moose_vars[i]))->sln()[_qp];

    SamplerBase::addSample(_q_point[_qp], _current_elem->id(), _values);
  }
}

void
SideValueSampler_crab::finalize()
{
  SamplerBase::finalize();
}

void
SideValueSampler_crab::threadJoin(const UserObject & y)
{
  const auto & vpp = static_cast<const SideValueSampler_crab &>(y);

  SamplerBase::threadJoin(vpp);
}
