And the source code of the hfConvection.C and hfConvection.h as follows:
hfConvection.C
{

#pragma once

#include "hfConvection.h"

#include "Material.h"
#include "MooseMesh.h"
#include "MooseVariable.h"

registerMooseObject("hmch",hfConvection);

InputParameters
hfConvection::validParams() 
  InputParameters params = Kernel::validParams();
  //addClassDescription
  params.addClassDescription("The convection term of the equation of momentum");
  params.addRequiredParam<unsigned int>("component",
      "The component (0 for x, 1 for y and 2 for z) of P");
  return params;
}

hfConvection::hfConvection(const InputParameters & parameters)
  : Kernel(parameters),
   //_lamda(getMaterialProperty<Real>("lamda")),
   _miu(getMaterialProperty<Real>("miu")),
   _component(getParam<unsigned int>("component"))
{
  if (_component >= _mesh.dimension())
    paramError("component", "The component cannot be greater than the mesh dimension");
}

Real
hfConvection::computeQpResidual()
{
  // (lamda+miu) * _grad_u[_qp]*_test[_i][_qp](component),散度
  return _miu[_qp]* _grad_u[_qp] * _test[_i][_qp](_component);
}

Real
hfConvection::computeQpJacobian()
{
  // the partial derivative of _grad_u is just _grad_phi[_j](component)
  return _miu[_qp] * _grad_phi[_j][_qp] * _test[_i][_qp](_component);
}

}

hfConvection.h
{

#pragma once 

#include "Kernel.h" 

class hfConvection : public Kernel
{
public:
  static InputParameters validParams();
  hfConvection(const InputParameters & parameters);

protected:

  virtual Real computeQpResidual() override;
  virtual Real computeQpJacobian() override;

private:

 // const MaterialProperty<Real> & _lamda;
  const MaterialProperty<Real> & _miu;
  const unsigned int _component;/// The spatial component

};

}
