void
GrandPotentialSink::initialSetup()
{
  validateNonlinearCoupling<Real>("Rho");
  validateNonlinearCoupling<Real>("hm");
  validateNonlinearCoupling<Real>("Rhos");
  validateNonlinearCoupling<Real>("kiv");
  validateNonlinearCoupling<Real>("kxs");
}

Real
GrandPotentialSink::computeQpResidual()
{
  return hm[_qp] * kiv[_qp] * Rho[_qp] * Rhos[_qp] + hm[_qp] * kxs[_qp] * Rho[_qp];
}

Real
GrandPotentialSink::computeQpJacobian()
{
  return hm[_qp] * kiv[_qp] * dRhodv[_qp] * Rhos[_qp] * _phi[_j][_qp] + 
         hm[_qp] * kiv[_qp] * Rho[_qp] * dRhosdv[_qp] * _phi[_j][_qp] + 
         hm[_qp] * kxs[_qp] * dRhodv[_qp] * _phi[_j][_qp];
}

Real
GrandPotentialSink::computeQpOffDiagJacobian(unsigned int jvar)
{
  if (jvar == _v_var_num)
  {
    Real sum = dhmdeta[_qp] * kiv[_qp] * Rho[_qp] * Rhos[_qp] +
               hm[_qp] * kiv[_qp] * dRhodv[_qp] * Rhos[_qp] +
               hm[_qp] * kiv[_qp] * Rho[_qp] * dRhosdv[_qp] +
               dhmdeta[_qp] * kxs[_qp] * Rho[_qp] +
               hm[_qp] * kxs[_qp] * dRhodv[_qp];

    return _phi[_j][_qp] * sum * _test[_i][_qp];
  }

  return 0.0;
}

