PorousFlowCapillaryPressureVG.h

#include "PorousFlowCapillaryPressureVG_alpha.h"
#include "PorousFlowVanGenuchten.h"
#include "MaterialProperty.h"

registerMooseObject("PorousFlowApp", PorousFlowCapillaryPressureVG_alpha);
InputParameters
PorousFlowCapillaryPressureVG_alpha::validParams()
{
  InputParameters params = PorousFlowCapillaryPressure::validParams();
  params.addRequiredRangeCheckedParam<Real>(
      "m",
      "m >= 0 & m <= 1",
      "van Genuchten exponent m. Must be between 0 and 1, and optimally should be set to >0.5");
  // * Cancel the data format check (maybe the function can only check the scalar value instead of a
  // matrix)
  // params.addRequiredRangeCheckedParam<Real>(
  //     "alpha", "alpha > 0", "van Genuchten parameter alpha. Must be positive");
  // Change alpha to be a required material property
  params.addRequiredCoupledVar(
      "alpha", "Name of the material property providing the spatially varying alpha");
  params.addRangeCheckedParam<Real>("s_scale",
                                    1.0,
                                    "s_scale > 0.0 & s_scale <= 1.0",
                                    "CapillaryPressure = f(Seff * s_scale) - "
                                    "f(s_scale), where f is the van Genuchten "
                                    "expression.  Setting s_scale<1 is unusual "
                                    "but sometimes helps fully saturated, "
                                    "2-phase PP simulations converge as the "
                                    "zero derivative (1/f'(S=1)=0) is removed");
  params.addClassDescription("van Genuchten capillary pressure");
  return params;
}

PorousFlowCapillaryPressureVG_alpha::PorousFlowCapillaryPressureVG_alpha(
    const InputParameters & parameters)
  : PorousFlowCapillaryPressure(parameters),
    _m(getParam<Real>("m")),
    // _alpha(getParam<Real>("alpha")),
    _alpha(coupledValue("alpha")),
    _s_scale(getParam<Real>("s_scale"))
// _pc_sscale(PorousFlowVanGenuchten::capillaryPressure(_s_scale, _alpha[0], _m, _pc_max))
{
}

Real
PorousFlowCapillaryPressureVG_alpha::capillaryPressureCurve(Real saturation, unsigned qp) const
{
  const Real seff = effectiveSaturationFromSaturation(saturation) * _s_scale;
  return PorousFlowVanGenuchten::capillaryPressure(seff, _alpha[qp], _m, _pc_max) -
         PorousFlowVanGenuchten::capillaryPressure(_s_scale, _alpha[qp], _m, _pc_max);
}

Real
PorousFlowCapillaryPressureVG_alpha::dCapillaryPressureCurve(Real saturation, unsigned qp) const
{
  const Real seff = effectiveSaturationFromSaturation(saturation) * _s_scale;
  return PorousFlowVanGenuchten::dCapillaryPressure(seff, _alpha[qp], _m, _pc_max) * _dseff_ds *
         _s_scale;
}

Real
PorousFlowCapillaryPressureVG_alpha::d2CapillaryPressureCurve(Real saturation, unsigned qp) const
{
  const Real seff = effectiveSaturationFromSaturation(saturation) * _s_scale;
  return PorousFlowVanGenuchten::d2CapillaryPressure(seff, _alpha[qp], _m, _pc_max) * _dseff_ds *
         _dseff_ds * _s_scale * _s_scale;
}

Real
PorousFlowCapillaryPressureVG_alpha::effectiveSaturation(Real pc, unsigned qp) const
{
  return (1.0 / _s_scale) *
         PorousFlowVanGenuchten::effectiveSaturation(
             pc - PorousFlowVanGenuchten::capillaryPressure(_s_scale, _alpha[qp], _m, _pc_max),
             _alpha[qp],
             _m);
}

Real
PorousFlowCapillaryPressureVG_alpha::dEffectiveSaturation(Real pc, unsigned qp) const
{
  return (1.0 / _s_scale) *
         PorousFlowVanGenuchten::dEffectiveSaturation(
             pc - PorousFlowVanGenuchten::capillaryPressure(_s_scale, _alpha[qp], _m, _pc_max),
             _alpha[qp],
             _m);
}

Real
PorousFlowCapillaryPressureVG_alpha::d2EffectiveSaturation(Real pc, unsigned qp) const
{
  return (1.0 / _s_scale) *
         PorousFlowVanGenuchten::d2EffectiveSaturation(
             pc - PorousFlowVanGenuchten::capillaryPressure(_s_scale, _alpha[qp], _m, _pc_max),
             _alpha[qp],
             _m);
}

