Question
Hi, there is a custom Diffusion Kernel. Seems all is right, because it's quite simple.
#pragma once

#include "GenericKernel.h"

template <bool is_ad>
class OxygenDiffusionTempl : public GenericKernel<is_ad>
{
public:
  static InputParameters validParams();
  OxygenDiffusionTempl(const InputParameters & parameters);

protected:
  virtual GenericReal<is_ad> computeQpResidual() override;
  virtual Real computeQpJacobian() override;

  const VariableValue & _temperature; //[K]
  usingGenericKernelMembers;
  using GenericKernel<is_ad>::_grad_test;
  using GenericKernel<is_ad>::_grad_u;
  using GenericKernel<is_ad>::_grad_phi;
private:
  Real _oxygen_diffusion_coeff; //[m^2/s]
};

typedef OxygenDiffusionTempl<false> OxygenDiffusion;
typedef OxygenDiffusionTempl<true> ADOxygenDiffusion;
#include "OxygenDiffusion.h"

registerMooseObject("MiracleApp", OxygenDiffusion);
registerMooseObject("MiracleApp", ADOxygenDiffusion);

template <bool is_ad>
InputParameters OxygenDiffusionTempl<is_ad>::validParams()
{
  InputParameters params = GenericKernel<is_ad>::validParams();
  params.addCoupledVar("temperature", "the temperature of the MOX or UO2 fuel in K");
  params.addClassDescription("Compute the oxygen diffusion term in MOX fuel.");
  return params;
}

template <bool is_ad>
OxygenDiffusionTempl<is_ad>::OxygenDiffusionTempl(const InputParameters & parameters)
: GenericKernel<is_ad>(parameters),
_temperature(this->coupledValue("temperature"))
{
}

template <bool is_ad>
GenericReal<is_ad> OxygenDiffusionTempl<is_ad>::computeQpResidual()
{
  _oxygen_diffusion_coeff = 3.5e-9 * std::exp(-7500. / _temperature[_qp]);// modifyed according FEAST_OXIDAL paper  
  return _oxygen_diffusion_coeff * _grad_test[_i][_qp] * _grad_u[_qp];
}

template <bool is_ad>
Real OxygenDiffusionTempl<is_ad>::computeQpJacobian()
{
  if constexpr (!is_ad)
  return _oxygen_diffusion_coeff * _grad_test[_i][_qp] * _grad_phi[_j][_qp];
  else
  mooseError("In ADOxygenDiffusion, computeQpJacobian should  not be called!");
}

template class OxygenDiffusionTempl<false>;
template class OxygenDiffusionTempl<true>;
